action.body:
		try
		{
			var wrpcontent = new XPCNativeWrapper(this.getElementById('dashboard_frame').contentWindow).wrappedJSObject;
   			wrpcontent.Dashboard.refresh();
	    }
	    catch (e)
	    {
	    	wCore.error('refresh', [], e);
	    }

    	wCore.debug('dashboard.editDashboard');
    	var oldUrl = this.getElementById('designDasboardFrame').getAttribute("src");
    	
    	editUrl = wToolkit.buildXulURL('dashboard', 'EditContent', {lang: Context.W_LANG, uilang: Context.W_UILANG}, true);
    	var deck = this.getElementById('pageDeck');
		if (editUrl != oldUrl)
		{
			deck.selectedIndex = 3;
			this.getElementById('designDasboardFrame').setAttribute("src", editUrl);
		}
		else
		{
			deck.selectedIndex = 1;
		}
		this.getElementById('dashboard_frame').setAttribute("src", "about:blank");

	try
	{	
		wCore.debug('dashboard.onModuleInit');
		var oldUrl = this.getElementById('dashboard_frame').getAttribute("src");
		
		wCore.debug('dashboard.onModuleInit oldurl :' + oldUrl);
		var dashboardRegularUrl = wToolkit.buildXulURL('dashboard', 'Dashboard', {lang: Context.W_LANG, uilang: Context.W_UILANG}, 'html');
		
		wCore.debug('dashboard.onModuleInit RegularUrl :' + dashboardRegularUrl);
		if (oldUrl != dashboardRegularUrl)
		{	
			wCore.debug('dashboard.onModuleInit setSrc' + dashboardRegularUrl);	    
	    	this.getElementById('dashboard_frame').setAttribute("src", dashboardRegularUrl);
	    }
	    
		var deck = this.getElementById('pageDeck');
		wCore.debug('dashboard.onModuleInit deck');
		
		deck.selectedIndex = 0;
		wCore.debug('dashboard.onModuleInit selectedIndex');
	
    }
    catch (e)
    {
    	wCore.error('onModuleInit', [], e);
    }
